import { IStorage } from '@typings/i-typings';
import { parse } from 'papaparse';
import * as yup from 'yup';

export type FileProcessingResults = {
  result: string;
} & (
  | {
      success: false;
      errors: string[];
    }
  | {
      success: true;
    }
);

export class PersistenceHelpers {
  constructor(private storage: IStorage) {}

  /**
   * Save CSV data to DynamoDB according to a yup schema
   */
  async saveCSVToDynamoDb(
    tableName: string,
    schema: yup.ObjectSchema<any>,
    csv: Buffer | string,
  ): Promise<FileProcessingResults> {
    const parsedData = parse(csv.toString(), {
      header: true,
      skipEmptyLines: true,
    });

    if (parsedData.errors.length) {
      return {
        result: 'failed to ingest csv',
        success: false,
        errors: parsedData.errors.map((error) => error.message),
      };
    }

    return this.saveRecordsToDynamoDB(tableName, schema, parsedData.data);
  }

  /**
   * Save records to DynamoDB according to a yup schema
   */
  async saveRecordsToDynamoDB<T>(
    tableName: string,
    schema: yup.ObjectSchema<any>,
    data: T[],
  ): Promise<FileProcessingResults> {
    const errors: string[] = [];
    const records: T[] = [];

    // Validate each row of data
    for (const row of data) {
      try {
        records.push(await schema.validate(row));
      } catch (error) {
        errors.push(error.message);
      }
    }

    if (errors.length) {
      return {
        result: 'failed to ingest csv',
        success: false,
        errors,
      };
    }

    const results = await this.storage.dynamoDb.batchPutItem(
      tableName,
      records,
    );

    results.forEach((it) => {
      if (it.err) {
        errors.push(String(it.err));
      }
    });

    if (errors.length > 0) {
      return {
        result: 'failed to save items',
        success: false,
        errors: errors,
      };
    }

    return {
      result: `${records.length} rows ingested`,
      success: true,
    };
  }


  async loadRecordsFromDynamoDB<T>(
    tableName: string,
    schema: yup.SchemaOf<T>,
  ): Promise<T[]> {
    const items = await this.storage.dynamoDb.getAll<T>(tableName);
    return items.filter((it) => schema.isValidSync(it));
  }
}
